import React from "react";
import "./App.css";

function App() {
  const downloadClick = () => {
    console.log("hello");
    fetch("/download")
      // Convert data into a 'blob'.
      .then((res) => res.blob())
      .then((blob) => {
        // Create blob link for download.
        const url = window.URL.createObjectURL(new Blob([blob]));
        const link = document.createElement("a");
        link.href = url;
        link.setAttribute("download", "hi.docx");

        // Append to html page.
        document.body.appendChild(link);

        // Force download.
        link.click();

        // Clean up and remove link from dom.
        link.parentNode.removeChild(link);
      });
  };

  return (
    <div className="App">
      <button onClick={downloadClick}>Download File</button>
    </div>
  );
}

export default App;
